import React, {useState} from 'react'

export default function ScreenAddMovie() {

    let [title, setTitle] = useState('')
    let [category, setCategory] = useState('')

    return (
        <div>
            <input value={title} placeholder='title' onChange={(event) => setTitle(event.target.value)}/>
            <br/>
            <select value={category} onChange={(event) => setCategory(event.target.value)}>
                <option value="">--Please choose an option--</option>
                <option value={category}>Comedy</option>
                <option value={category}>Animation</option>
                <option value={category}>Thriller</option>
                <option value={category}>Drame</option>
            </select>
            <br/>
            <button >Ajouter le film</button>
            <br/>
            contenu = {title}
            <br/>
            cat = {category}
        </div>
    )

}
